svg.script:
var phrase="";
function type(evt)
{
	svgdoc=evt.target.ownerDocument;
	key=evt.getCharCode();
	if (key==8) 
	{
		if (phrase!="") 
		{
			phrase=phrase.substring(0,phrase.length-1);
		}
	}
	else 
	{
		letter=String.fromCharCode(key);
		alert(key);
		phrase=phrase+letter;
	}
	obj=svgdoc.getElementById("enter");
	child=obj.firstChild.setData(phrase);
}
